mapper.update:
UPDATE TBL_USER 
SET CNT = CNT + 1
WHERE USERID = #{id}

UPDATE TBL_USER 
SET CNT = 0
WHERE USERID = #{id}

mapper.select:
SELECT *
FROM TBL_USER
WHERE USERID = #{userId} AND NAME = #{name} AND REPLACE(PHONE, '-', '') = #{phone}

mapper.update:
UPDATE TBL_USER
SET PASSWORD = #{hashPwd}
WHERE USERID = #{userId}
